<?php defined('SYSPATH') OR die('No direct access allowed.');class Discount_user_Driver extends Discount_Driver{	public function amount(){		$discountedUsers = $this->getObjects();		if (in_array($this->user->id, $discountedUsers) && $this->evalCondition()){			$this->appliedDiscounts[] = $this->discount->name;			$this->calc($this->cart->subtotal());			return $this->sum;		}else{			return 0;		}	}}	