<?php defined('SYSPATH') OR die('No direct access allowed.');class Orders_baskets_data_Model extends ORM {	protected $belongs_to = array('orders_basket');		public function find($id = NULL) {		$res = parent::find($id);				$res->data = json_decode($res->data,TRUE);				return $res;	}		public function save() {		if (is_array($this->data))			$this->data = json_encode($this->data);				parent::save();				$this->data = json_decode($this->data,TRUE);	}}