<?php	class Catalog_Controller extends Controller {				public function index(){		$this->page();	}			public function page($page_no = 1)	{				$items_per_page = 2;		$cat_id = 2;				$offset = ($page_no - 1) * $items_per_page;			$limit = $items_per_page;						$category = new Category_Model($cat_id);		//$category = ORM::factory('category')->with('catagories_description')->find($cat_id); //without the lazy loading				echo $category->name .'<br>';		echo $category->catagories_description->description .'<br>';				$catalog_list = $category->limit($limit, $offset)->products;		//$catalog_list = $category->limit($limit, $offset)->like('products.name', 'ca')->products;		//$catalog_list = $category->limit($limit, $offset)->where('product_id >', 1)->products;		$catalog_total = $category->count_last_query();		echo 'this category has '. $catalog_total .' related products';						foreach($catalog_list as $p){			echo $p->name.'<br>';			echo $p->products_description->description.'<br>';			print_r($p->attributes);		}				 		$pagination = new Pagination(array(			'uri_segment'    => 'page',			'total_items'    => $catalog_total,			'items_per_page' => $items_per_page,			'style'          => 'classic',			'auto_hide'      => TRUE,		));				echo $pagination->render();		$profile = new Profiler;		}}		?>